import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import { userActions } from '../../redux/actions';

class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        // reset login status
        this.props.dispatch(userActions.logout());

        this.state = {
            mobileNo: '',
            password: '',
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { mobileNo, password } = this.state;
        const { dispatch } = this.props;
		console.log(mobileNo, password);
        if (mobileNo && password) {
            dispatch(userActions.login(mobileNo, password));
        }
    }

    render() {
        const { loggingIn } = this.props;
        const { mobileNo, password, submitted } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Web Checkin</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !mobileNo ? ' has-error' : '')}>
                        <label htmlFor="mobileNo">Mobile Number</label>
                        <input type="text" className="form-control" name="mobileNo" value={mobileNo} onChange={this.handleChange} />
                        {submitted && !mobileNo &&
                            <div className="help-block">Mobile number is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">Login</button>
                        {loggingIn &&
                            <img src="data:image/gif;base64," />
                        }
                        <Link to="/register" className="btn btn-link">Book Flight</Link>
                    </div>
                </form>
            </div>
        );
    }
}

function mapStateToProps(state) {
    const { loggingIn } = state.authentication;
    return {
        loggingIn
    };
}

const connectedLoginPage = connect(mapStateToProps)(LoginPage);
export { connectedLoginPage as LoginPage }; 
